const { new_conf } = require("./rollup.shared");
const uglify = require("rollup-plugin-uglify");

const conf = new_conf();
conf.plugins.push(uglify());

for (let out of conf.output) {
  let fname = out.file;
  out.file = fname.slice(0, fname.lastIndexOf(".js")) + ".min.js";
}

module.exports = conf;
